import React, { useState } from 'react';
import axios from 'axios';
import './App.css';

// Definimos la URL base de nuestra API de Laravel
const API_URL = 'http://127.0.0.1:8000/api';

function App() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [name, setName] = useState(''); // Para el registro

  const [message, setMessage] = useState('');
  const [token, setToken] = useState(null);

  const handleLogin = async (e) => {
    e.preventDefault();
    setMessage('');
    try {
      const response = await axios.post(`${API_URL}/login`, {
        email: email,
        password: password
      });
      setToken(response.data.access_token);
      setMessage('¡Login exitoso!');
      console.log('Token:', response.data.access_token);
    } catch (error) {
      console.error('Error en el login:', error.response.data);
      setMessage('Error en el login: ' + (error.response.data.message || 'Credenciales incorrectas'));
    }
  };

  const handleRegister = async (e) => {
    e.preventDefault();
    setMessage('');
    try {
      const response = await axios.post(`${API_URL}/register`, {
        name: name,
        email: email,
        password: password
      });
      setMessage(response.data.message);
      console.log('Registro exitoso:', response.data);
    } catch (error) {
      console.error('Error en el registro:', error.response.data);
      let errorMsg = 'Error en el registro. ';
      if (error.response.data.errors) {
        // Formatear los errores de validación de Laravel
        errorMsg += Object.values(error.response.data.errors).flat().join(' ');
      }
      setMessage(errorMsg);
    }
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>Test de Entorno: Login Dentista</h1>

        <div className="form-container">
          {/* Formulario de Registro */}
          <form onSubmit={handleRegister}>
            <h2>Registrarse</h2>
            <input 
              type="text" 
              placeholder="Nombre" 
              value={name} 
              onChange={(e) => setName(e.target.value)} 
              required 
            />
            <input 
              type="email" 
              placeholder="Email" 
              value={email} 
              onChange={(e) => setEmail(e.target.value)} 
              required 
            />
            <input 
              type="password" 
              placeholder="Contraseña (mínimo 8 caracteres)" 
              value={password} 
              onChange={(e) => setPassword(e.target.value)} 
              required 
            />
            <button type="submit">Registrar</button>
          </form>

          {/* Formulario de Login */}
          <form onSubmit={handleLogin}>
            <h2>Iniciar Sesión</h2>
            <input 
              type="email" 
              placeholder="Email" 
              value={email} 
              onChange={(e) => setEmail(e.target.value)} 
              required 
            />
            <input 
              type="password" 
              placeholder="Contraseña" 
              value={password} 
              onChange={(e) => setPassword(e.target.value)} 
              required 
            />
            <button type="submit">Login</button>
          </form>
        </div>

        {message && <p className="message">{message}</p>}
        {token && <p className="token-display">Token Obtenido: {token.substring(0, 30)}...</p>}
      </header>
    </div>
  );
}

export default App;
